mapper.select:
WITH ranked_data AS ( SELECT blog_domain_name, active_time, type, ROW_NUMBER() OVER (PARTITION BY blog_domain_name ORDER BY active_time DESC) as rn FROM ( SELECT m.blog_domain_name, m.created_at as active_time, 'moment' as type FROM moment m INNER JOIN blog b ON m.blog_domain_name = b.domain_name WHERE m.deleted = false AND b.draft = false AND b.deleted = false AND b.valid = true AND b.gravatar_valid = true
UNION ALL
SELECT p.blog_domain_name, p.published_at as active_time, 'post' as type FROM post p INNER JOIN blog b ON p.blog_domain_name = b.domain_name WHERE p.deleted = false AND p.draft = false AND b.draft = false AND b.deleted = false AND b.valid = true AND b.gravatar_valid = true ) all_activities ), latest_activities AS ( SELECT blog_domain_name as blogDomainName, MAX(active_time) as latestActiveTime, MAX(CASE WHEN rn = 1 THEN type END) as type FROM ranked_data GROUP BY blog_domain_name ) SELECT blogDomainName, latestActiveTime, type FROM latest_activities ORDER BY latestActiveTime DESC LIMIT #{limit}

SELECT

FROM blog
WHERE draft=false
AND deleted=false
AND gravatar_valid=#{gravatarValid}

    AND domain_name NOT IN

        #{item}


ORDER BY RAND() LIMIT #{limit}

SELECT
EXISTS (
SELECT 1 FROM blog WHERE domain_name=#{domainName}
)

SELECT
EXISTS (
SELECT 1 FROM blog WHERE rss_address=#{rssAddress}
)

mapper.update:
UPDATE blog
SET
gravatar_valid=#{gravatarValid}
WHERE domain_name=#{domainName}
